async function login(){let t=document.getElementById("Email").value,i=document.getElementById("Password").value,r=document.getElementsByName("__RequestVerificationToken")[0].value,n=new FormData;n.append("Email",t);n.append("Password",i);n.append("__RequestVerificationToken",r);try{await fetch("/Auth/Login",{method:"POST",body:n}).then(async function(n){if(n.ok)window.location.href="/";else{let t=await n.text();alert(t)}})}catch(u){}}var XHR=null,settings;const LOGINBTN=document.getElementById("loginBtn");document.querySelectorAll("[data-val]").forEach(n=>{n.addEventListener("keyup",()=>{let n=$(document.getElementById("fields"));n.valid()?LOGINBTN.removeAttribute("disabled"):LOGINBTN.disabled="disabled"})});$("#fields").submit(function(n){n.preventDefault();login()});settings={validClass:"is-valid",errorClass:"is-invalid"};$.validator.setDefaults(settings);$.validator.unobtrusive.options=settings;